package hackerrank;

import java.util.HashMap;
import java.util.Map;

public class BearAndSteadyGene {

    //   l
    // G A A A T A A A
    //           r
    //
    // [{A:6},{C:0},{G:1},{T:1}]
    // [{A:6},{C:0},{G:1},{T:1}]
    //
    static int steadyGene(String s) {
        Map<Character, Integer> map = new HashMap<>();
        map.put('A', 0);
        map.put('C', 0);
        map.put('G', 0);
        map.put('T', 0);
        s.chars().forEach(c -> map.put((char)c, map.get((char)c) + 1));

        int min = s.length();
        int left = 0;
        for (int right = 0 ; right < s.length() - 1 ; right++) {
            char ch = s.charAt(right);
            map.put(ch, map.get(ch) - 1);
            while (steady(map, s)) {
                min = Math.min(min, right - left + 1);
                char key = s.charAt(left);
                map.put(key, map.get(key) + 1);
                left++;
            }
        }
        return min;
    }

    private static boolean steady(Map<Character, Integer> map, String s) {
        for (Integer i : map.values())
            if (i > s.length() / 4) {
                return false;
            }
        return true;
    }

    public static void main(String[] args) {
        System.out.println(
                steadyGene("GAAATAAA")
        );
//        System.out.println(
//                steadyGene("")
//        );
    }
}
